mapper.select:
SELECT * FROM
(SELECT WRITER, NO, title, content, REGDATE, FLAG_NQ, HIT,
ROW_NUMBER() OVER (ORDER BY NO DESC) AS RNUM
FROM board_normal
WHERE FLAG_NQ = #{flag_nq}

    AND TITLE LIKE '%'|| #{searchKeyword} ||'%' ORDER BY NO DESC


    AND CONTENT LIKE '%'|| #{searchKeyword} ||'%' ORDER BY NO DESC

)
WHERE RNUM BETWEEN #{rowStart} AND #{rowEnd}

SELECT COUNT(NO) FROM board_normal WHERE FLAG_NQ = #{flag_nq}
AND TITLE LIKE '%'|| #{searchKeyword} ||'%' ORDER BY NO DESC
AND CONTENT LIKE '%'|| #{searchKeyword} ||'%' ORDER BY NO DESC

mapper.update:
UPDATE BOARD_NORMAL
SET HIT = HIT + 1
WHERE NO = #{no}

UPDATE FILE_TABLE
SET DEL_YN = 'Y'
WHERE FILE_NO = #{no}

UPDATE FILE_TABLE
SET DEL_YN = 'Y'
WHERE BOARD_NO = #{no}

mapper.select:
SELECT * FROM (SELECT PRODUCT_NO,PRODUCT_NAME,ID,DEADLINE,GOAL_MONEY,CONTENT,REGION,SAVE_MONEY,SUPPORTER,PIMG_NO , ROW_NUMBER() OVER (ORDER BY PRODUCT_NO DESC) AS RNUM FROM FUNDING_OPEN WHERE 1=1
AND REGION LIKE '%'|| #{searchKeyword} ||'%' ORDER BY PRODUCT_NO DESC
AND PRODUCT_NAME LIKE '%'|| #{searchKeyword} ||'%' ORDER BY PRODUCT_NO DESC
) WHERE RNUM BETWEEN #{rowStart} AND #{rowEnd}

SELECT * FROM (SELECT ID, NAME, PRICE, DESCRIPTION, URL, REGDATE , ROW_NUMBER() OVER (ORDER BY ID DESC) AS RNUM FROM PRODUCT WHERE 1=1
AND NAME LIKE '%'|| #{searchKeyword} ||'%' ORDER BY ID DESC
AND DESCRIPTION LIKE '%'|| #{searchKeyword} ||'%' ORDER BY ID DESC
) WHERE RNUM BETWEEN #{rowStart} AND #{rowEnd}
